import React from "react";
import { View, SafeAreaView, FlatList } from "react-native";
import { SIZES, dummyData } from "../constants";
import {
  HomeCategoryHeader,
  HomeHeader,
  HomeSearchBar,
  HomeSeeRecipeCard,
  HomeTrendingRecipes,
  CategoryCard,
} from "../components/Home";

const Home = ({ navigation }: { navigation: any }) => {
  return (
    <SafeAreaView
      style={{
        flex: 1,
        backgroundColor: "white",
      }}
    >
      <FlatList
        data={dummyData.categories}
        keyExtractor={(item) => `${item.id}`}
        keyboardDismissMode="on-drag"
        showsVerticalScrollIndicator={false}
        ListHeaderComponent={
          <View>
            {/* Header */}
            <HomeHeader />
            {/* Search Bar */}
            <HomeSearchBar />
            {/* See Recipe Card You Haven't Tried*/}
            <HomeSeeRecipeCard />
            {/* Trending */}
            <HomeTrendingRecipes navigation={navigation} />
            {/* Category Header */}
            <HomeCategoryHeader />
          </View>
        }
        renderItem={({ item }) => {
          return (
            <CategoryCard
              containerStyle={{
                marginHorizontal: SIZES.padding,
              }}
              categoryItem={item}
              onPress={() => navigation.navigate("Recipe", { recipe: item })}
            />
          );
        }}
        ListFooterComponent={
          <View
            style={{
              marginBottom: 100,
            }}
          />
        }
      />
    </SafeAreaView>
  );
};

export default Home;
