import React from "react";
import { View, Text, Image } from "react-native";
import { COLORS, FONTS, icons, SIZES } from "../../constants";

interface RecipeCardDetails {
  recipeItem: any;
}
const RecipeCardDetails = ({ recipeItem }: RecipeCardDetails) => {
  return (
    <View
      style={{
        flex: 1,
      }}
    >
      {/* Name and Bookmark */}
      <View
        style={{
          flex: 1,
          flexDirection: "row",
          justifyContent: "space-between",
        }}
      >
        <Text
          style={{
            width: "70%",
            color: COLORS.white,
            ...FONTS.h3,
            fontSize: 18,
          }}
        >
          {recipeItem.name}
        </Text>

        <Image
          source={recipeItem.isBookmark ? icons.bookmarkFilled : icons.bookmark}
          style={{
            width: 20,
            height: 20,
            marginRight: SIZES.base,
            tintColor: COLORS.darkGreen,
          }}
        />
      </View>
      {/* Duration & Serving */}
      <Text
        style={{
          color: COLORS.gray,
          ...FONTS.body4,
        }}
      >
        {recipeItem.duration} | {recipeItem.serving} serving
      </Text>
    </View>
  );
};

export default RecipeCardDetails;
